package week04_1_operator;

public class Ex04_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 관계 연산자
		int a=100,b=200;
		
		System.out.printf("%d == %d 는 %s \n",a,b,a==b);
		System.out.printf("%d >= %d 는 %s \n",a,b,a>=b);
		System.out.printf("%d > %d 는 %s \n",a,b,a>b);
		System.out.printf("%d != %d 는 %s \n",a,b,a!=b);

	}

}
