package week04_1_operator;

public class Ex04_06_LogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 논리 연산자
		int a= 99;
		
		System.out.printf(" and 연산: %s\n",(a>=100)&&(a<100));
		System.out.printf(" or 연산: %s\n",(a>=100)||(a<100));
		System.out.printf(" not 연산: %s\n",!(a>=100));

	}

}
